/**
 * Reports Page
 */

import React from 'react';
import { Box, Typography, styled } from '@mui/material';
import { motion } from 'framer-motion';
import { cyberColors, designTokens, glassmorphism } from '../utils/theme';
import { pageVariants } from '../utils/animations';
import { Card } from '../components/common/Card';

const PageContainer = styled(motion.div)({
  padding: 24,
});

const Title = styled(Typography)({
  fontFamily: designTokens.typography.fontFamily.display,
  fontSize: '1.75rem',
  fontWeight: 700,
  color: cyberColors.text.primary,
  marginBottom: 24,
});

const ReportCard = styled(motion.div)({
  ...glassmorphism.card,
  padding: 20,
  borderRadius: designTokens.borderRadius.md,
  marginBottom: 16,
  cursor: 'pointer',
  '&:hover': {
    borderColor: cyberColors.neon.cyan,
  },
});

// Mock data
const reports = [
  { id: '1', title: 'Threat Assessment Report', investigation: 'suspicious-domain.com', date: '2024-01-15' },
  { id: '2', title: 'Executive Summary', investigation: '192.168.1.100', date: '2024-01-14' },
  { id: '3', title: 'Technical Analysis', investigation: 'threat-actor@example.com', date: '2024-01-13' },
];

const ReportsPage: React.FC = () => {
  return (
    <PageContainer
      variants={pageVariants}
      initial="initial"
      animate="enter"
      exit="exit"
    >
      <Title>Reports</Title>

      {reports.map((report, index) => (
        <ReportCard
          key={report.id}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: index * 0.1 }}
        >
          <Typography
            sx={{
              fontFamily: designTokens.typography.fontFamily.display,
              color: cyberColors.text.primary,
              fontSize: '1.1rem',
              fontWeight: 600,
              mb: 1,
            }}
          >
            {report.title}
          </Typography>
          <Typography variant="body2" sx={{ color: cyberColors.text.secondary }}>
            Investigation: {report.investigation} | Generated: {report.date}
          </Typography>
        </ReportCard>
      ))}

      {reports.length === 0 && (
        <Card variant="glass">
          <Typography color="text.secondary" textAlign="center">
            No reports generated yet.
          </Typography>
        </Card>
      )}
    </PageContainer>
  );
};

export default ReportsPage;
